mapper.sql:
SELECT T.ID, T.USER_ID, T.GOODS_ID, T.GOODS_SN, T.GOODS_NAME, T.SKU_ID, T.MARKET_PRICE, T.RETAIL_PRICE, T.NUMBER, T.GOODS_SPECIFITION_NAME_VALUE, T.CHECKED, T.LIST_PIC_URL, T.SHOPS_ID, T.CREATE_TIME, U.NICKNAME, S.NAME SHOPS_NAME, S.SHOPS_SN, G.BRAND_ID, G.IS_ON_SALE, GS.MIN_SELL FROM MALL_CART T LEFT JOIN MALL_USER U ON T.USER_ID = U.ID LEFT JOIN MALL_SHOPS S ON T.SHOPS_ID = S.ID LEFT JOIN MALL_GOODS G ON T.GOODS_ID = G.ID LEFT JOIN MALL_GOODS_SKU GS ON GS.ID = T.SKU_ID WHERE 1=1
AND T.CHECKED = #{params.checked}
AND T.USER_ID = #{params.userId}
AND T.GOODS_ID = #{params.goodsId}
AND T.SHOPS_ID = #{params.shopsId}
AND U.NICKNAME LIKE '%${params.nickname}%'
