mapper.sql:
SELECT T.ID, T.USER_ID, T.SUPERIOR_ID, T.NAME, T.IS_AUDIT, T.JOIN_TIME, T.AMOUNT_AVAILABLE, T.AMOUNT_WITHDRAWN, T.AMOUNT_TOTAL, T.INVITATION_CODE, U.NICKNAME, UU.NICKNAME SUPERIOR_NICKNAME FROM MALL_DIST T JOIN MALL_USER U ON T.USER_ID = U.ID LEFT JOIN MALL_DIST TT ON T.SUPERIOR_ID = TT.ID LEFT JOIN MALL_USER UU ON TT.USER_ID = UU.ID WHERE 1=1
AND U.NICKNAME LIKE '%${params.nickname}%'
AND T.INVITATION_CODE = #{params.code}

mapper.update:
UPDATE MALL_DIST
SET
AMOUNT_AVAILABLE = AMOUNT_AVAILABLE - ${deductionAmount},
AMOUNT_WITHDRAWN = AMOUNT_WITHDRAWN + ${amount}
WHERE USER_ID = #{userId}

UPDATE MALL_DIST
SET
AMOUNT_AVAILABLE = AMOUNT_AVAILABLE + ${income},
AMOUNT_TOTAL = AMOUNT_TOTAL + ${income}
WHERE USER_ID = #{userId}
