mapper.sql:
SELECT T.ID, T.USER_ID, T.GOODS_ID, T.ADD_TIME, U.NICKNAME, U.HEAD_IMG_URL, G.NAME GOODS_NAME, G.LIST_PIC_URL, G.RETAIL_PRICE, G.SALES, G.MARKET_PRICE, G.GOODS_BRIEF FROM MALL_FOOTPRINT T LEFT JOIN MALL_USER U ON T.USER_ID = U.ID LEFT JOIN MALL_GOODS G ON T.GOODS_ID = G.ID WHERE 1=1 and G.IS_DELETE = 1
AND U.NICKNAME LIKE '%${params.nickname}%'
and T.USER_ID = #{params.userId}
and T.GOODS_ID = #{params.goodsId}
and G.IS_ON_SALE = #{params.isOnSale}
AND ADD_TIME > #{params.starTime}

mapper.select:
SELECT NAME GOODS_NAME,COUNT(1) GOODS_SUM FROM MALL_FOOTPRINT LEFT JOIN MALL_GOODS ON MALL_GOODS.ID = MALL_FOOTPRINT.GOODS_ID WHERE 1=1
AND ADD_TIME > #{params.starTime}
AND ADD_TIME < #{params.endTime}
group by GOODS_ID
