SELECT T.ID, T.NAME, T.SHOPS_ID, T.CATEGORY_ID, T.SHOPS_CATEGORY_ID, T.GOODS_SN, T.BRAND_ID, T.GOODS_NUMBER, T.IS_HOT, T.IS_ON_SALE, T.IS_NEW, T.IS_LIMITED, T.IS_GROUP, T.GROUP_NUMBER, T.GROUP_PRICE, T.IS_DELETE, T.LIST_PIC_URL, T.KEYWORDS, T.GOODS_BRIEF, T.UNIT_PRICE, T.MARKET_PRICE, T.RETAIL_PRICE, T.COUNTER_PRICE, T.MIN_SELL, T.IS_APP_EXCLUSIVE, T.APP_EXCLUSIVE_PRICE, T.GOODS_DESC, T.SORT, T.PROMOTION_TAG, T.PROMOTION_DESC, T.SALES, T.CREATE_USER_ID, T.CREATE_TIME, T.CREATE_USER_ORG_NO, T.UPDATE_USER_ID, T.UPDATE_TIME, T.VIDEO_URL, C.NAME CATEGORY_NAME, B.NAME BRAND_NAME, D.name SHOPS_NAME, D.SHOPS_SN SHOPS_SN FROM MALL_GOODS T LEFT JOIN MALL_CATEGORY C ON T.CATEGORY_ID = C.ID LEFT JOIN MALL_BRAND B ON T.BRAND_ID = B.ID LEFT JOIN MALL_SHOPS D ON T.SHOPS_ID = D.ID WHERE 1=1 AND IS_DELETE=1
AND T.NAME LIKE '%${params.name}%'
AND T.GOODS_SN LIKE '%${params.goodsSn}%'
AND T.SHOPS_ID = #{params.shopsId}
AND T.SHOPS_CATEGORY_ID = #{params.shopsCategoryId}
AND T.IS_ON_SALE = #{params.isOnSale}
AND T.IS_GROUP = #{params.isGroup}
AND T.ID = #{params.goodsId}
and T.CATEGORY_ID in
#{item}
