mapper.sql:
SELECT T.ID, T.FROM_TYPE, T.ORDER_TYPE, T.ORDER_SN, T.USER_ID, T.ORDER_STATUS, T.SHIPPING_STATUS, T.PAY_STATUS, T.PAY_TYPE, T.CONSIGNEE, T.COUNTRY, T.PROVINCE, T.CITY, T.DISTRICT, T.ADDRESS, T.POSTAL_CODE, T.MOBILE, T.POSTSCRIPT, T.SHIPPING_ID, T.SHIPPING_NAME, T.SHIPPING_CODE, T.SHIPPING_NO, T.SHIPPING_FEE, T.PREPAY_ID, T.ACTUAL_PRICE, T.INTEGRAL_MONEY, T.ORDER_PRICE, T.ADD_TIME, T.CONFIRM_TIME, T.PAY_TIME, T.COUPON_ID, T.COUPON_PRICE, T.PARENT_ID, T.SHOPS_ID, T.GOODS_ID, T.EXPIRE_TIME, U.NICKNAME, U.USER_NAME, S.NAME SHOPS_NAME, S.SHOPS_SN FROM MALL_ORDER T LEFT JOIN MALL_USER U ON T.USER_ID = U.ID LEFT JOIN MALL_SHOPS S ON T.SHOPS_ID = S.ID WHERE 1=1
AND U.NICKNAME LIKE '%${params.nickname}%'
AND T.ORDER_SN LIKE '%${params.orderSn}%'
AND T.USER_ID = #{params.userId}
AND S.USER_ID = #{params.sysUserId}
AND T.FROM_TYPE = #{params.fromType}
AND T.ORDER_STATUS = #{params.orderStatus}
AND T.ORDER_TYPE = #{params.orderType}
AND T.PAY_STATUS = #{params.payStatus}
AND T.SHIPPING_STATUS = #{params.shippingStatus}
AND T.CONSIGNEE LIKE '%${params.consignee}%'
AND T.MOBILE LIKE '%${params.mobile}%'
AND T.ORDER_TYPE != 3
AND NOT EXISTS(SELECT 1 FROM MALL_COMMENT WHERE MALL_COMMENT.ORDER_ID = T.ID)
AND T.ORDER_STATUS >= 400
AND T.SHOPS_ID = #{params.shopsId}
AND T.PARENT_ID IS NOT NULL

mapper.select:
SELECT COUNT(A.ID) AS orders, SUM(CASE A.ORDER_STATUS WHEN 0 THEN 1 ELSE 0 END ) AS unPayNum, SUM(CASE A.ORDER_STATUS WHEN 201 THEN 1 ELSE 0 END ) AS unSendNum, SUM(CASE A.ORDER_STATUS WHEN 300 THEN 1 ELSE 0 END ) AS unTakeNum, SUM(CASE A.ORDER_STATUS>=400 WHEN true THEN 1 ELSE 0 END) AS shNum, (SELECT COUNT(1) FROM MALL_ORDER O LEFT JOIN MALL_COMMENT C ON C.ORDER_ID = O.ID WHERE O.ORDER_STATUS = 301 AND C.ID IS NULL
and O.USER_ID = #{userId}
) AS unEvalNum FROM MALL_ORDER A WHERE 1 = 1 AND A.ORDER_TYPE != 3 AND A.PARENT_ID IS NOT NULL
and A.USER_ID = #{userId}

SELECT G.GOODS_NAME, COUNT(G.GOODS_ID) GOODS_COUNT, SUM(O.ACTUAL_PRICE) PRICE_SUM
FROM MALL_ORDER O
LEFT JOIN MALL_ORDER_GOODS G ON O.ID = G.ORDER_ID
WHERE O.SHOPS_ID IS NOT NULL
AND O.PAY_STATUS = 3

    AND O.SHOPS_ID = #{params.shopsId}


    AND O.ADD_TIME >= #{params.startTime}


    AND O.ADD_TIME <= #{params.endTime} 

GROUP BY G.GOODS_ID

SELECT S.NAME, SUM(O.ACTUAL_PRICE) PRICE_SUM FROM MALL_ORDER O LEFT JOIN MALL_SHOPS S ON O.SHOPS_ID = S.ID WHERE O.SHOPS_ID IS NOT NULL AND O.PAY_STATUS = 3
AND O.ADD_TIME >= #{params.startTime}
AND O.ADD_TIME <= #{params.endTime}
GROUP BY O.SHOPS_ID

SELECT SUM(O.ACTUAL_PRICE) PRICE_SUM
FROM MALL_ORDER O
LEFT JOIN MALL_ORDER_GOODS G ON O.ID = G.ORDER_ID
WHERE O.SHOPS_ID IS NOT NULL
AND O.PAY_STATUS = 3 and O.ORDER_STATUS = 301

    AND O.SHOPS_ID = #{params.shopsId}


    AND O.ADD_TIME >= #{params.startTime}


    AND O.ADD_TIME <= #{params.endTime}
