mapper.sql:
SELECT T.ID, T.USER_ID, T.COUPON_ID, T.ADD_TIME, T.TYPE, T.STATUS, T.USED_TIME, T.ORDER_ID, U.NICKNAME, O.ORDER_SN, C.TITLE, C.COUPON_SN, C.COUPON_TYPE, C.MIN_PRICE, C.SUB_PRICE, C.DISCOUNT, C.BEGIN_TIME, C.END_TIME, C.LIMIT_TYPE FROM MALL_USER_COUPON T LEFT JOIN MALL_USER U ON U.ID = T.USER_ID LEFT JOIN MALL_COUPON C ON C.ID = T.COUPON_ID LEFT JOIN MALL_ORDER O ON O.ID = T.ORDER_ID WHERE 1=1 AND C.ID IS NOT NULL
AND T.ID = #{params.id}
AND T.USER_ID = #{params.userId}
AND C.LIMIT_TYPE = #{params.limitType}
AND (EXISTS (SELECT 1 FROM MALL_COUPON_GOODS WHERE MALL_COUPON_GOODS.COUPON_ID = T.COUPON_ID AND MALL_COUPON_GOODS.GOODS_ID IN
#{goodsId}
) OR EXISTS (SELECT 1 FROM MALL_COUPON_BRAND WHERE MALL_COUPON_BRAND.COUPON_ID = T.COUPON_ID AND MALL_COUPON_BRAND.BRAND_ID IN
#{brandId}
))
AND U.NICKNAME LIKE '%${params.nickname}%'
AND T.TYPE = #{params.type}
AND C.COUPON_SN LIKE '%${params.couponSn}%'
AND T.STATUS = #{params.status}
AND C.BEGIN_TIME <= #{params.nowDate} AND C.END_TIME >= #{params.nowDate}
