SELECT T.ID, T.USER_NAME, T.PASSWORD, T.GENDER, T.BIRTHDAY, T.REGISTER_TIME, T.LAST_LOGIN_TIME, T.LAST_LOGIN_IP, T.USER_LEVEL_ID, T.NICKNAME, T.MOBILE, T.REGISTER_IP, T.HEAD_IMG_URL, T.OPEN_ID, T.MP_OPEN_ID, T.UNION_ID, T.SUBSCRIBE, T.SUBSCRIBE_TIME, T.INTEGRAL, T.BALANCE, L.NAME USER_LEVEL_NAME FROM MALL_USER T LEFT JOIN MALL_USER_LEVEL L ON T.USER_LEVEL_ID = L.ID WHERE 1=1
AND T.USER_NAME LIKE '%${params.userName}%'
AND T.MOBILE LIKE '%${params.mobile}%'
AND T.GENDER = #{params.gender}
and T.MP_OPEN_ID in
#{openid}
